import React, { useState } from "react";
import user from "../images/user.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faImage } from "@fortawesome/free-regular-svg-icons";
import axios from 'axios'
const Posts = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async (event) => {
    event.preventDefault();

    // Make a GET request to the Nominatim API
    try {
      const response = await axios.get('https://nominatim.openstreetmap.org/search', {
        params: {
          q: searchQuery,
          format: 'json',
          addressdetails: 1,
          limit: 5,
        },
      });

      setResults(response.data);
    } catch (error) {
      console.error('Error fetching data from Nominatim API:', error);
    }
  };
  return (
    <div className="relative">
      <div className="absolute w-[90%] m-[5%] top-[95px] bg-gray-100 p-[3%] rounded-[10px]">
        <div className="px-[4%] py-[2%] bg-white rounded-[10px]">
          <div className="flex justify-between pb-[3%] border-b-[1px] " >
            <div className="h-[60px]">
              <img className="h-full" src={user} alt="" />
            </div>
            <input
              className="w-[93%] px-[3%] bg-gray-100 font-QRegular outline-none rounded-[10px]"
              type="text"
              placeholder="What do you want to share today..!"
            />
          </div>
          <div className="flex justify-between mt-[3%]" >
            <div className="file-input w-[11%] flex justify-between items-center">
              <input type="file" id="file" class="file" />
              <FontAwesomeIcon className="text-orange-400" icon={faImage} />
              <label className="cursor-pointer font-QSemi " for="file">Photo/Video</label>
            </div>
            <div>
      <h2>Search Locations</h2>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Enter a location"
        />
        <button type="submit">Search</button>
      </form>

      {/* Display search results */}
      <ul>
        {results.map((result) => (
          <li key={result.place_id}>
            {result.display_name}
          </li>
        ))}
      </ul>
    </div>
            <button className="bg-[#8735C8] font-QSemi text-white px-[5%] py-[1%] rounded-[10px] " >Post</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Posts;
