import React, { useState } from "react";
import user from "../images/user.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faImage } from "@fortawesome/free-regular-svg-icons";
const Posts = () => {
  const [location, setLocation] = useState({
    latitude: '',
    longitude: '',
  });

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        (error) => {
          console.error("Error fetching location: ", error);
        }
      );
    } else {
      alert('Geolocation is not supported by this browser.');
    }
  };
  return (
    <div className="relative">
      <div className="absolute w-[90%] m-[5%] top-[95px] bg-gray-100 p-[3%] rounded-[10px]">
        <div className="px-[4%] py-[2%] bg-white rounded-[10px]">
          <div className="flex justify-between pb-[3%] border-b-[1px] " >
            <div className="h-[60px]">
              <img className="h-full" src={user} alt="" />
            </div>
            <input
              className="w-[93%] px-[3%] bg-gray-100 font-QRegular outline-none rounded-[10px]"
              type="text"
              placeholder="What do you want to share today..!"
            />
          </div>
          <div className="flex justify-between mt-[3%]" >
            <div className="file-input w-[11%] flex justify-between items-center">
              <input type="file" id="file" class="file" />
              <FontAwesomeIcon className="text-orange-400" icon={faImage} />
              <label className="cursor-pointer font-QSemi " for="file">Photo/Video</label>
            </div>
            <div className="location-input-container">
      <h1>Find Your Location</h1>

      {/* Button to get current location */}
      <button onClick={getLocation}>Get My Location</button>

      {/* Display latitude and longitude */}
      <div className="location-input-fields">
        <label htmlFor="latitude">Latitude:</label>
        <input
          type="text"
          id="latitude"
          value={location.latitude}
          readOnly
        />

        <label htmlFor="longitude">Longitude:</label>
        <input
          type="text"
          id="longitude"
          value={location.longitude}
          readOnly
        />
      </div>
    </div>
            <button className="bg-[#8735C8] font-QSemi text-white px-[5%] py-[1%] rounded-[10px] " >Post</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Posts;
