import React from "react";
import user from "../images/user.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faImage } from "@fortawesome/free-regular-svg-icons";
import { GoogleMap, useLoadScript, Autocomplete } from '@react-google-maps/api';

const libraries = ['places'];

const mapContainerStyle = {
  width: '100%',
  height: '400px',
};

const defaultCenter = {
  lat: 37.7749, // Default center (San Francisco)
  lng: -122.4194,
};
const Posts = () => {
  const [center, setCenter] = useState(defaultCenter);
  const [autocomplete, setAutocomplete] = useState(null);
  const mapRef = useRef();

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'YOUR_GOOGLE_MAPS_API_KEY', // Replace with your actual API key
    libraries,
  });

  const onAutocompleteLoad = (autoC) => {
    setAutocomplete(autoC);
  };

  const onPlaceChanged = () => {
    if (autocomplete !== null) {
      const place = autocomplete.getPlace();
      const location = place.geometry?.location;
      if (location) {
        setCenter({
          lat: location.lat(),
          lng: location.lng(),
        });
        mapRef.current.panTo({
          lat: location.lat(),
          lng: location.lng(),
        });
      }
    } else {
      console.log('Autocomplete is not loaded yet!');
    }
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps...</div>;
  return (
    <div className="relative">
      <div className="absolute w-[90%] m-[5%] top-[95px] bg-gray-100 p-[3%] rounded-[10px]">
        <div className="px-[4%] py-[2%] bg-white rounded-[10px]">
          <div className="flex justify-between pb-[3%] border-b-[1px] " >
            <div className="h-[60px]">
              <img className="h-full" src={user} alt="" />
            </div>
            <input
              className="w-[93%] px-[3%] bg-gray-100 font-QRegular outline-none rounded-[10px]"
              type="text"
              placeholder="What do you want to share today..!"
            />
          </div>
          <div className="flex justify-between mt-[3%]" >
            <div className="file-input w-[11%] flex justify-between items-center">
              <input type="file" id="file" class="file" />
              <FontAwesomeIcon className="text-orange-400" icon={faImage} />
              <label className="cursor-pointer font-QSemi " for="file">Photo/Video</label>
            </div>
            <div>
      <h1>Location Search</h1>

      {/* Autocomplete search input */}
      <Autocomplete onLoad={onAutocompleteLoad} onPlaceChanged={onPlaceChanged}>
        <input
          type="text"
          placeholder="Search for a location"
          style={{
            boxSizing: `border-box`,
            border: `1px solid transparent`,
            width: `240px`,
            height: `32px`,
            padding: `0 12px`,
            borderRadius: `3px`,
            boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
            fontSize: `14px`,
            outline: `none`,
            textOverflow: `ellipses`,
            position: "relative",
            top: "10px",
          }}
        />
      </Autocomplete>

      {/* Google Map */}
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={10}
        center={center}
        onLoad={(map) => (mapRef.current = map)}
      />
    </div>
            <button className="bg-[#8735C8] font-QSemi text-white px-[5%] py-[1%] rounded-[10px] " >Post</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Posts;
